  color: #000;
}

html {
  background-color: #f7f7f9;
  font-family: "Noto Sans";
}

body {
  margin: 0;
}

#app {
  display: flex;
}

.btn {
  background-color: #fff;
  padding: 10px 10px;
  border-radius: 10px;
  font-size: 14px;
}

.btn-accent {
  background-color: #62b2ae;
  color: #fff;
}

.btn-icon-plus {
  position: relative;
}

.btn-icon-plus::before {
  content: "+";
  margin-right: 5px;
}

.navbar-wrapper {
  min-width: 120px;
}

.navbar {
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #fff;
  padding: 15px;
  margin: 0;
  list-style: none;
  height: 100vh;
  font-family: "Playfair Display";
  font-size: 12px;
  position: fixed;
}

.content-wrapper {
  overflow: hidden;
  padding: 10px;
  width: calc(100vw - 120px);
}

.table-header {
  display: flex;
  align-items: center;
  padding: 0 30px;
}

.table-wrapper {
  overflow: auto;
  padding: 0 30px;
  width: 100%;
}

.menu-logo {
  width: 40px;
  margin-bottom: 50px;
}

.menu-item:not(.menu-logo) {
  margin-bottom: 30px;
}

.menu-item a {
  display: flex;
  flex-direction: column;
  align-items: center;
  color: #c0c0c0;
  transition-duration: 0.2s;
}

.menu-item .router-link-active {
  color: #000;
}

.menu-item:not(.menu-logo) svg {
  fill: #c0c0c0;
  width: 20px;
  margin-bottom: 5px;
  transition-duration: 0.2s;
}

.menu-item:not(.menu-logo) .router-link-active svg {
  fill: #000;
}

.menu-item:not(.menu-logo):hover a,
.menu-item:not(.menu-logo):hover svg {
  color: #000;
  fill: #000;
}

th {
  font-size: 12px;
  color: #808080;
}

th,
td {
  text-align: start;
  font-weight: 300;
  padding: 10px 15px 10px 0;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

tbody tr {
  background-color: #fff;
  height: 50px;
  padding: 5px 0;
  width: 100%;
}

table {
  border-collapse: separate;
  border-spacing: 0 20px;
  width: 100%;
}

tr td:first-child {
  border-top-left-radius: 10px;
}
tr td:last-child {
  border-top-right-radius: 10px;
}
tr td:first-child {
  border-bottom-left-radius: 10px;
}
tr td:last-child {
  border-bottom-right-radius: 10px;
}

.inner-row {
  position: relative;
}
